mapper.select:
select  * from tb_cashier


        and cashier_name = #{username}


ORDER BY cashier_name desc limit #{currentPage},#{pageSize}

mapper.update:
update tb_cashier
set  cashier_state = '0'
where cashier_account = #{username}

update tb_cashier
set  cashier_state = '1'
where cashier_account = #{username}
